mapper.select:
select tpd_.* from pd_task_pickup_dispatch tpd_ left join pd_order o_ on tpd_.order_id = o_.id left join pd_transport_order to_ on o_.id = to_.order_id
and tpd_.courier_id = #{params.courierId}
and tpd_.status = #{params.status}
and tpd_.task_type = #{params.taskType}
and tpd_.actual_end_time >= STR_TO_DATE(CONCAT(#{params.date},' ','00:00:00'), '%Y-%m-%d %H:%i:%s') and tpd_.actual_end_time <= STR_TO_DATE(CONCAT(#{params.date},' ','23:59:59'), '%Y-%m-%d %H:%i:%s')
and ( o_.sender_name LIKE CONCAT('%',#{params.keyword},'%') or o_.sender_phone LIKE CONCAT('%',#{params.keyword},'%') or to_.id LIKE CONCAT('%',#{params.keyword},'%') )
order by tpd_.create_time desc
